iot-reward-shares:
query IotRewardShares($after: String, $before: String, $first: Int, $last: Int, $startPeriod: ISO8601DateTime!, $endPeriod: ISO8601DateTime!, $hotspotKey: String, $rewardType: String) {
  iotRewardShares(after: $after, before: $before, first: $first, last: $last, startPeriod: $startPeriod, endPeriod: $endPeriod, hotspotKey: $hotspotKey, rewardType: $rewardType) {
    nodes {
        startPeriod
      endPeriod
      rewardType
      unallocatedRewardType
      amount
      hotspotKey
      beaconAmount
      witnessAmount
      dcTransferAmount
      manifest {
          id
        writtenFiles
        startTimestamp
        endTimestamp
        rewardData {
          ... on MobileRewardData {
            rewardType
          pocBonesPerRewardShare
          boostedPocBonesPerRewardShare
          serviceProviderPromotions {
              serviceProvider
            incentiveEscrowFundBps
            promotions {
                entity
              startTs
              endTs
              shares
            }
          }
          token
        }
          ... on IotRewardData {
            rewardType
          pocBonesPerBeaconRewardShare
          pocBonesPerWitnessRewardShare
          dcBonesPerShare
          token
        }
        }
        epoch
        price
        deduplicationKey
        fileCategory
        fileName
      }
    }
    pageInfo {
        hasNextPage
      hasPreviousPage
      startCursor
      endCursor
    }
  }
}

reward-manifests:
query RewardManifests($after: String, $before: String, $first: Int, $last: Int, $startTimestamp: ISO8601DateTime!, $endTimestamp: ISO8601DateTime!) {
